mapper.select:
select id, name, email, isAdmin
from user
where delflag = 0 and id=#{id} and password=#{password}

mapper.update:
update user
set password=#{password}, email=#{email}
where id=#{id}

mapper.select:
select id, name, email, joindate
from user
where id=#{id}

select password
from user
where id=#{id} and email=#{email} and name=#{name}

select token
from user
where id = #{id}

mapper.update:
update user
set token = #{token}
where id = #{id}

update user
set token = #{token, jdbcType=VARCHAR}
where id = #{id}
