from user
where delflag = 0 and id=#{id} and password=#{password}

mapper.update:
update user
set name=#{name}, email=#{email}

	, password=#{password}

where id=#{id}

mapper.select:
select count(*)
from user
where id=#{id}

select id, name, email, joindate
from user
where id=#{id}

select password
from user
